import { Link, useNavigate } from "react-router-dom";
import "./Login.scss";
import { useState } from "react";

const Login = () => {
  const [notValidLogin, setNotValidLogin] = useState(false);
  const navigate = useNavigate();

  const handleSubmite = async (e: any) => {
    e.preventDefault();
    const email = e?.target?.email?.value;
    const password = e?.target?.password?.value;

    await fetch(`http://localhost:2224/login`, {
      method: "POST",
      headers: { "Content-Type": "Application/json" },
      body: JSON.stringify({ email, password }),
    })
      .catch((e) => alert(e))
      .then((res) => res?.json())
      .then((data) => (data._id ? navigate("/home") : setNotValidLogin(true)));
  };

  return (
    <form className="login-form" onSubmit={handleSubmite}>
      <input
        name="email"
        className="field"
        type="email"
        placeholder="email"
        required
      />
      <input
        name="password"
        className="field"
        type="string"
        placeholder="password"
        required
      />

      <button type="submit" className="button">
        add
      </button>
      <Link to="/registr" title="login">
        Registration
      </Link>
    </form>
  );
};

export default Login;
