import { Link, useNavigate } from "react-router-dom";
import "./Login.scss";
import { useState } from "react";

const Login = () => {
  const navigate = useNavigate();

  const handleLogin = async (e: any) => {
    e.preventDefault();
    const idInstance = '1101820440'
    // e?.target?.idInstance?.value;
    const apiTokenInstance = 'db2e9708829d4a749e06ab9e627c889df84a67706cb3401f9b'
    // e?.target?.apiTokenInstance?.value;
    const SendMessage = 'Hello World'

    await fetch(`https://api.green-api.com/waInstance${idInstance}/SendMessage/${apiTokenInstance}`, {
      method: "POST",
      headers: { "Content-Type": "Application/json" },
      // body: JSON.stringify({ idInstance, apiTokenInstance }),
    })
      .catch((e) => alert(e))
      .then((res) => res?.json())
      .then((data) => console.log(data));
  };

  return (
    <form className="login-form" onSubmit={handleLogin}>
      <input
        name="idInstance"
        className="field"
        type="text"
        placeholder="idInstance"
        required
      />
      <input
        name="apiTokenInstance"
        className="field"
        type="text"
        placeholder="apiTokenInstance"
        required
      />

      <button type="submit" className="button">
        add
      </button>

    </form>
  );
};

export default Login;
