import { Link, useNavigate } from "react-router-dom";
import "./MessageSend.scss";
import { useState } from "react";

interface Message {
  message: string;
  my: boolean;
}

const MessageSend = () => {
  const navigate = useNavigate();
  const [allMessage, setAllMessage] = useState<Message[]>([]);
  const handleLogin = async (e: any) => {
    e.preventDefault();
    const idInstance = "1101820565";
    // e?.target?.idInstance?.value;
    const apiTokenInstance =
      "ff1b1d87e69d40d2a91f520ed9d2085a6fbc79cc00f4470ead";
    // e?.target?.apiTokenInstance?.value;
    const message = e?.target?.message?.value;
    const chatId = `${e?.target?.phoneNumber?.value}@c.us`;

    await fetch(
      `https://api.green-api.com/waInstance${idInstance}/SendMessage/${apiTokenInstance}`,
      {
        method: "POST",
        headers: { "Content-Type": "Application/json" },
        body: JSON.stringify({ message, chatId }),
      }
    )
      .catch((e) => alert(e))
      .then((res) => res?.json())
      .then((data) => {
        if (data.idMessage)
          setAllMessage([...allMessage, { message, my: true }]);
      });
  };

  return (
    <section>
      <form className="login-form" onSubmit={handleLogin}>
        <input
          name="idInstance"
          className="field"
          type="text"
          placeholder="idInstance"
          required
        />

        <input
          name="apiTokenInstance"
          className="field"
          type="text"
          placeholder="apiTokenInstance"
          required
        />

        <textarea
          name="message"
          className="field"
          placeholder="message"
          required
        />

        <input
          name="phoneNumber"
          className="field"
          type="text"
          placeholder="phoneNumber"
          required
        />

        <button type="submit" className="button">
          Send Message
        </button>
      </form>
      <div>-----------------------------------------</div>
      <div className="message-block">
        {allMessage.map((el) => (
          <div className="message">{el.message}</div>
        ))}
      </div>
    </section>
  );
};

export default MessageSend;
